@import "tailwindcss";
@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

:root {
  /* Updated color tokens to match Ubuntu theme with orange primary and terminal aesthetics */
  --background: oklch(1 0 0);
  /* White background */
  --foreground: oklch(0.2 0 0);
  /* Dark gray text */
  --card: oklch(0.97 0 0);
  /* Light gray card background */
  --card-foreground: oklch(0.3 0 0);
  /* Medium gray card text */
  --popover: oklch(1 0 0);
  /* White popover */
  --popover-foreground: oklch(0.2 0 0);
  /* Dark gray popover text */
  --primary: oklch(0.6 0.2 35);
  /* Ubuntu Orange #E95420 */
  --primary-foreground: oklch(1 0 0);
  /* White text on orange */
  --secondary: oklch(0.97 0 0);
  /* Light gray secondary */
  --secondary-foreground: oklch(0.2 0 0);
  /* Dark gray secondary text */
  --muted: oklch(0.97 0 0);
  /* Light gray muted */
  --muted-foreground: oklch(0.3 0 0);
  /* Medium gray muted text */
  --accent: oklch(0.3 0 0);
  /* Medium gray accent */
  --accent-foreground: oklch(1 0 0);
  /* White accent text */
  --destructive: oklch(0.6 0.2 15);
  /* Red destructive */
  --destructive-foreground: oklch(1 0 0);
  /* White destructive text */
  --border: oklch(0.9 0 0);
  /* Light border */
  --input: oklch(1 0 0);
  /* White input */
  --ring: oklch(0.6 0.2 35);
  /* Ubuntu Orange ring */
  --chart-1: oklch(0.6 0.2 35);
  /* Ubuntu Orange chart */
  --chart-2: oklch(0.97 0 0);
  /* Light gray chart */
  --chart-3: oklch(0.2 0 0);
  /* Dark gray chart */
  --chart-4: oklch(0.3 0 0);
  /* Medium gray chart */
  --chart-5: oklch(1 0 0);
  /* White chart */
  --radius: 0.5rem;
  /* Slightly smaller radius for terminal feel */
  --sidebar: oklch(0.97 0 0);
  /* Light gray sidebar */
  --sidebar-foreground: oklch(0.2 0 0);
  /* Dark gray sidebar text */
  --sidebar-primary: oklch(0.6 0.2 35);
  /* Ubuntu Orange sidebar primary */
  --sidebar-primary-foreground: oklch(1 0 0);
  /* White sidebar primary text */
  --sidebar-accent: oklch(0.3 0 0);
  /* Medium gray sidebar accent */
  --sidebar-accent-foreground: oklch(1 0 0);
  /* White sidebar accent text */
  --sidebar-border: oklch(0.9 0 0);
  /* Light sidebar border */
  --sidebar-ring: oklch(0.6 0.2 35);
  /* Ubuntu Orange sidebar ring */
  --font-ubuntu: "Ubuntu", sans-serif;
  --font-mono: "Ubuntu Mono", monospace;
}

.dark {
  /* Updated dark mode colors for Ubuntu terminal aesthetic */
  --background: oklch(0.15 0 0);
  /* Dark terminal background */
  --foreground: oklch(0.95 0 0);
  /* Light terminal text */
  --card: oklch(0.18 0 0);
  /* Slightly lighter card background */
  --card-foreground: oklch(0.95 0 0);
  /* Light card text */
  --popover: oklch(0.15 0 0);
  /* Dark popover */
  --popover-foreground: oklch(0.95 0 0);
  /* Light popover text */
  --primary: oklch(0.6 0.2 35);
  /* Ubuntu Orange remains same */
  --primary-foreground: oklch(1 0 0);
  /* White text on orange */
  --secondary: oklch(0.25 0 0);
  /* Dark gray secondary */
  --secondary-foreground: oklch(0.95 0 0);
  /* Light secondary text */
  --muted: oklch(0.25 0 0);
  /* Dark gray muted */
  --muted-foreground: oklch(0.7 0 0);
  /* Medium light muted text */
  --accent: oklch(0.25 0 0);
  /* Dark gray accent */
  --accent-foreground: oklch(0.95 0 0);
  /* Light accent text */
  --destructive: oklch(0.5 0.2 15);
  /* Darker red destructive */
  --destructive-foreground: oklch(0.95 0 0);
  /* Light destructive text */
  --border: oklch(0.25 0 0);
  /* Dark border */
  --input: oklch(0.25 0 0);
  /* Dark input */
  --ring: oklch(0.6 0.2 35);
  /* Ubuntu Orange ring */
  --chart-1: oklch(0.6 0.2 35);
  /* Ubuntu Orange chart */
  --chart-2: oklch(0.7 0.15 180);
  /* Blue chart */
  --chart-3: oklch(0.8 0.15 120);
  /* Green chart */
  --chart-4: oklch(0.65 0.2 300);
  /* Purple chart */
  --chart-5: oklch(0.7 0.2 60);
  /* Yellow chart */
  --sidebar: oklch(0.18 0 0);
  /* Dark sidebar */
  --sidebar-foreground: oklch(0.95 0 0);
  /* Light sidebar text */
  --sidebar-primary: oklch(0.6 0.2 35);
  /* Ubuntu Orange sidebar primary */
  --sidebar-primary-foreground: oklch(1 0 0);
  /* White sidebar primary text */
  --sidebar-accent: oklch(0.25 0 0);
  /* Dark sidebar accent */
  --sidebar-accent-foreground: oklch(0.95 0 0);
  /* Light sidebar accent text */
  --sidebar-border: oklch(0.25 0 0);
  /* Dark sidebar border */
  --sidebar-ring: oklch(0.6 0.2 35);
  /* Ubuntu Orange sidebar ring */
}

@theme inline {
  /* Updated font variables to use Ubuntu fonts */
  --font-sans: var(--font-ubuntu);
  --font-mono: var(--font-mono);
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-destructive-foreground: var(--destructive-foreground);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }

  body {
    @apply bg-background text-foreground;
  }

  /* Added Ubuntu-specific terminal styling */
  .terminal {
    @apply font-mono bg-card text-card-foreground rounded-lg border border-border;
  }

  .ubuntu-orange {
    color: oklch(0.6 0.2 35);
  }

  .terminal-prompt::before {
    content: "$ ";
    @apply text-primary font-bold;
  }
}

/* Terminal Styles inspired by fkcodes.com */
.terminal-container {
  background: #000;
  color: #04ff00;
  font-family: ui-monospace, Consolas, "Cascadia Mono", Menlo, Monaco, "SFMono-Regular", "Liberation Mono", "Courier New", monospace;
  font-size: 14px;
  line-height: 1.4;
}

.terminal-container .index {
  color: #04ff00;
  font-weight: bold;
}

.terminal-container .color2 {
  color: #04ff00;
}

.terminal-container .command {
  color: #04ff00;
  font-weight: bold;
}

.terminal-container .error {
  color: #ff0000;
}

.terminal-container .inherit {
  color: #04ff00;
}

.terminal-container .margin {
  margin-left: 20px;
}

.terminal-container .no-animation {
  color: #04ff00;
}

.terminal-container a {
  color: #04ff00;
  text-decoration: underline;
}

.terminal-container a:hover {
  color: #ffffff;
}

.terminal-container p {
  margin: 0;
  padding: 0;
  white-space: pre;
  /* preserve all spaces exactly as typed */
  font-variant-ligatures: none;
  /* avoid ligature width changes */
  letter-spacing: 0;
  tab-size: 4;
}

/* Preserve spacing for ASCII lines rendered as <pre> and tighten vertical rhythm */
.terminal-container pre {
  margin: 0;
  padding: 0;
  white-space: pre;
  line-height: 1.15;
  font-variant-ligatures: none;
}

.terminal-container .cursor {
  position: relative;
  display: inline-block;
  width: 10px;
  height: 20px;
  background-color: #04ff00;
  animation: blink 1s infinite;
}

@keyframes blink {

  0%,
  50% {
    opacity: 1;
  }

  51%,
  100% {
    opacity: 0;
  }
}

.terminal-container .password {
  background-color: #ff0000;
}

.terminal-container input {
  background: transparent;
  border: none;
  outline: none;
  color: #04ff00;
  font-family: 'Courier New', monospace;
  font-size: 14px;
  width: 100%;
}

.terminal-container input::placeholder {
  color: #04ff00;
  opacity: 0.7;
}

/* Fix scrollbar positioning in terminal */
.terminal-container .overflow-y-auto {
  scrollbar-width: thin;
  scrollbar-color: #666 #000;
}

.terminal-container .overflow-y-auto::-webkit-scrollbar {
  width: 8px;
}

.terminal-container .overflow-y-auto::-webkit-scrollbar-track {
  background: #000;
}

.terminal-container .overflow-y-auto::-webkit-scrollbar-thumb {
  background: #666;
  border-radius: 4px;
}

.terminal-container .overflow-y-auto::-webkit-scrollbar-thumb:hover {
  background: #888;
}

import type React from "react";
import type { Metadata, Viewport } from "next";
import { Ubuntu, JetBrains_Mono } from "next/font/google";
import "./globals.css";
import PWARegister from "@/components/PWARegister";

const ubuntu = Ubuntu({
  subsets: ["latin"],
  weight: ["300", "400", "500", "700"],
  variable: "--font-ubuntu",
  display: "swap",
});

const jetbrainsMono = JetBrains_Mono({
  subsets: ["latin"],
  variable: "--font-mono",
  display: "swap",
});

export const metadata: Metadata = {
  title: "Ubuntu Portfolio | Developer Desktop",
  description:
    "Interactive Ubuntu-themed portfolio showcasing development projects and skills",
  generator: "v0.app",
  manifest: "/manifest.webmanifest",
  appleWebApp: {
    capable: true,
    statusBarStyle: "default",
    title: "Ubuntu Portfolio",
  },
  applicationName: "Ubuntu Portfolio",
  formatDetection: {
    telephone: false,
  },
  icons: {
    icon: [
      { url: "/icon-192x192.png", sizes: "192x192", type: "image/png" },
      { url: "/icon-512x512.png", sizes: "512x512", type: "image/png" },
    ],
    apple: [
      { url: "/apple-touch-icon.png", sizes: "180x180", type: "image/png" },
    ],
  },
};

export const viewport: Viewport = {
  themeColor: "#E95420",
  width: "device-width",
  initialScale: 1,
  maximumScale: 5,
  userScalable: true,
};

export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  return (
    <html lang="en" className={`${ubuntu.variable} ${jetbrainsMono.variable}`}>
      <head>
        <link rel="icon" href="/ubuntu-logo-circle.png" />
        <link rel="apple-touch-icon" href="/apple-touch-icon.png" />
      </head>
      <body className="font-sans antialiased dark">
        {children}
        <PWARegister />
      </body>
    </html>
  );
}

import type { MetadataRoute } from "next";

export default function manifest(): MetadataRoute.Manifest {
  return {
    name: "Ubuntu Portfolio | Developer Desktop",
    short_name: "Ubuntu Portfolio",
    description:
      "Interactive Ubuntu-themed portfolio showcasing development projects and skills",
    start_url: "/",
    display: "standalone",
    background_color: "#2C001E",
    theme_color: "#E95420",
    orientation: "portrait-primary",
    icons: [
      {
        src: "/icon-192x192.png",
        sizes: "192x192",
        type: "image/png",
        purpose: "maskable",
      },
      {
        src: "/icon-512x512.png",
        sizes: "512x512",
        type: "image/png",
        purpose: "maskable",
      },
      {
        src: "/icon-192x192.png",
        sizes: "192x192",
        type: "image/png",
        purpose: "any",
      },
      {
        src: "/icon-512x512.png",
        sizes: "512x512",
        type: "image/png",
        purpose: "any",
      },
    ],
  };
}

"use client"

import React, { useState, useRef, useEffect, useMemo } from "react"
import { Button } from "@/components/ui/button"
import TerminalComponent from "@/components/TerminalComponent"
import {
  Terminal,
  Folder,
  FileText,
  User,
  Code,
  Mail,
  ImageIcon,
  Settings,
  Github,
  Linkedin,
  Download,
  ExternalLink,
  Minus,
  Square,
  X,
  Home,
  Search,
  Trash2,
  HardDrive,
  HelpCircle,
  Monitor,
  Coffee,
  BookOpen,
  Zap,
  Star,
  Calculator,
  Music,
  Image,
  Globe,
  Camera,
  ChevronLeft,
  ChevronRight,
  Play,
  Pause,
  SkipBack,
  SkipForward,
  Volume2,
  Shuffle,
  Repeat,
  Bookmark,
  BookmarkCheck,
  Phone,
  MapPin
} from "lucide-react"

// Custom X Logo Component
const XLogo = ({ className }: { className?: string }) => (
  <svg viewBox="0 0 24 24" className={className} fill="currentColor">
    <path d="M18.244 2.25h3.308l-7.227 8.26 8.502 11.24H16.17l-5.214-6.817L4.99 21.75H1.68l7.73-8.835L1.254 2.25H8.08l4.713 6.231zm-1.161 17.52h1.833L7.084 4.126H5.117z" />
  </svg>
)

const UbuntuLoadingScreen = ({ onLoadingComplete }: { onLoadingComplete: () => void }) => {
  const [loadingProgress, setLoadingProgress] = useState(0)
  const [loadingText, setLoadingText] = useState("Starting Ubuntu...")

  useEffect(() => {
    const loadingSteps = [
      { progress: 20, text: "Loading kernel modules..." },
      { progress: 40, text: "Starting system services..." },
      { progress: 60, text: "Initializing desktop environment..." },
      { progress: 80, text: "Loading user interface..." },
      { progress: 95, text: "Almost ready..." },
      { progress: 100, text: "Welcome to Ubuntu!" },
    ]

    let currentStep = 0
    const progressTimer = setInterval(() => {
      if (currentStep < loadingSteps.length) {
        const step = loadingSteps[currentStep]
        setLoadingProgress(step.progress)
        setLoadingText(step.text)
        currentStep++
      } else {
        clearInterval(progressTimer)
        setTimeout(() => onLoadingComplete(), 800)
      }
    }, 300)

    return () => {
      clearInterval(progressTimer)
    }
  }, [onLoadingComplete])

  return (
    <div className="fixed inset-0 bg-black z-50 flex flex-col items-center justify-center">
      {/* Ubuntu Logo with pulsing animation */}
      <div className="mb-8 animate-pulse">
        <img
          src="/ubuntu-logo-circle.png"
          alt="Ubuntu"
          className="w-32 h-32 drop-shadow-2xl"
        />
      </div>

      {/* Loading dots animation */}
      <div className="flex space-x-2 mb-6">
        {[0, 1, 2].map((i) => (
          <div
            key={i}
            className="w-3 h-3 bg-[#E95420] rounded-full animate-bounce"
            style={{ animationDelay: `${i * 0.2}s` }}
          />
        ))}
      </div>

      {/* Progress bar */}
      <div className="w-80 h-2 bg-gray-800 rounded-full overflow-hidden mb-4">
        <div
          className="h-full bg-gradient-to-r from-[#E95420] to-[#F7A072] transition-all duration-300 ease-out"
          style={{ width: `${loadingProgress}%` }}
        />
      </div>

      {/* Loading text */}
      <p className="text-white text-lg font-light mb-2">{loadingText}</p>
      <p className="text-gray-400 text-sm">{loadingProgress}%</p>

      {/* Ubuntu branding */}
      <div className="absolute bottom-8 left-8 flex items-center gap-3">
        <img src="/ubuntu-logo-official.png" alt="Ubuntu" className="w-8 h-8" />
        <span className="text-white text-xl font-light">Ubuntu</span>
      </div>

      {/* Version info */}
      <div className="absolute bottom-8 right-8 text-gray-500 text-sm">
        Ubuntu 22.04.3 LTS
      </div>
    </div>
  )
}

interface Window {
  id: string
  title: string
  component: React.ReactNode
  position: { x: number; y: number }
  size: { width: number; height: number }
  isMinimized: boolean
  isMaximized: boolean
  zIndex: number
}

interface DesktopIcon {
  id: string
  name: string
  icon: React.ReactNode
  position: { x: number; y: number }
  action: () => void
}

interface DragState {
  isDragging: boolean
  draggedIcon: string | null
  offset: { x: number; y: number }
}

export default function UbuntuPortfolio() {
  const [isLoading, setIsLoading] = useState(true)
  const [currentTime, setCurrentTime] = useState("")
  const [isClient, setIsClient] = useState(false)
  const [currentWallpaper, setCurrentWallpaper] = useState("/assets/wallpapers/ubuntu-wallpaper.jpg")
  const [notifications, setNotifications] = useState<Array<{ id: string, message: string, type: 'info' | 'success' | 'warning' | 'error' }>>([])


  // Ubuntu-style sound effects (visual feedback)
  const playClickSound = () => {
    // Visual feedback for clicks
    const clickEffect = document.createElement('div')
    clickEffect.className = 'fixed inset-0 pointer-events-none z-50'
    clickEffect.innerHTML = '<div class="w-2 h-2 bg-white rounded-full animate-ping absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2"></div>'
    document.body.appendChild(clickEffect)
    setTimeout(() => clickEffect.remove(), 300)
  }

  const playHoverSound = () => {
    // Subtle hover effect
    const hoverEffect = document.createElement('div')
    hoverEffect.className = 'fixed inset-0 pointer-events-none z-40'
    hoverEffect.innerHTML = '<div class="w-1 h-1 bg-white/50 rounded-full animate-pulse absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2"></div>'
    document.body.appendChild(hoverEffect)
    setTimeout(() => hoverEffect.remove(), 200)
  }

  // Ubuntu-style notification system
  const showNotification = (message: string, type: 'info' | 'success' | 'warning' | 'error' = 'info') => {
    const id = Date.now().toString()
    setNotifications(prev => [...prev, { id, message, type }])
    setTimeout(() => {
      setNotifications(prev => prev.filter(n => n.id !== id))
    }, 4000)
  }
  const [windows, setWindows] = useState<Window[]>([])
  const [nextZIndex, setNextZIndex] = useState(1000)
  const [draggedIcon, setDraggedIcon] = useState<string | null>(null)
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 })
  const [draggedWindow, setDraggedWindow] = useState<string | null>(null)
  const [windowDragOffset, setWindowDragOffset] = useState({ x: 0, y: 0 })
  const [iconPositions, setIconPositions] = useState<Record<string, { x: number; y: number }>>({
    terminal: { x: 100, y: 100 },
    projects: { x: 100, y: 180 },
    notepad: { x: 100, y: 260 },
    about: { x: 100, y: 340 },
    skills: { x: 100, y: 420 },
    contact: { x: 250, y: 100 },
    gallery: { x: 250, y: 180 },
    settings: { x: 250, y: 260 },
  })
  const [terminalHistory, setTerminalHistory] = useState<string[]>([
    "Ubuntu 22.04.3 LTS ubuntu-developer tty1",
    "",
    "ubuntu-developer login: portfolio",
    "Welcome to Ubuntu Portfolio Desktop!",
    'Type "help" for available commands.',
    "",
  ])
  const [terminalInput, setTerminalInput] = useState("")
  const terminalRef = useRef<HTMLDivElement>(null)
  const terminalInputRef = useRef<HTMLInputElement>(null)
  const [currentCommand, setCurrentCommand] = useState("")

  const [desktopIcons, setDesktopIcons] = useState<DesktopIcon[]>([
    {
      id: "terminal",
      name: "Terminal",
      icon: (
        <div className="w-10 h-10 flex items-center justify-center">
          <img src="/assets/program-icons/terminal-app.png" alt="Terminal" className="w-8 h-8 drop-shadow-lg" />
        </div>
      ),
      position: { x: 100, y: 100 },
      action: () => openWindow("terminal", "Terminal", <TerminalWindow />),
    },
    {
      id: "projects",
      name: "Projects",
      icon: (
        <div className="w-10 h-10 flex items-center justify-center">
          <svg viewBox="0 0 24 24" className="w-8 h-8 drop-shadow-lg">
            <path fill="#E95420" d="M10 4H4c-1.11 0-2 .89-2 2v12c0 1.11.89 2 2 2h16c1.11 0 2-.89 2-2V8c0-1.11-.89-2-2-2h-8l-2-2z" />
            <path fill="#FFFFFF" d="M12 8v8l4-4-4-4z" />
          </svg>
        </div>
      ),
      position: { x: 100, y: 180 },
      action: () => openWindow("projects", "Projects", <ProjectsWindow />),
    },
    {
      id: "notepad",
      name: "Commands",
      icon: (
        <div className="w-10 h-10 flex items-center justify-center">
          <svg viewBox="0 0 24 24" className="w-8 h-8 drop-shadow-lg">
            <path fill="#7C3AED" d="M14,2H6A2,2 0 0,0 4,4V20A2,2 0 0,0 6,22H18A2,2 0 0,0 20,20V8L14,2M18,20H6V4H13V9H18V20Z" />
            <path fill="#FFFFFF" d="M8 12h8v2H8v-2zm0 4h8v2H8v-2zm0-8h5v5H8V8z" />
          </svg>
        </div>
      ),
      position: { x: 100, y: 260 },
      action: () => openWindow("notepad", "Commands Guide", <NotepadWindow />),
    },
    {
      id: "about",
      name: "About Me",
      icon: (
        <div className="w-10 h-10 flex items-center justify-center">
          <svg viewBox="0 0 24 24" className="w-8 h-8 drop-shadow-lg">
            <circle fill="#16A34A" cx="12" cy="12" r="10" />
            <circle fill="#FFFFFF" cx="12" cy="8" r="2" />
            <path fill="#FFFFFF" d="M12 12c-2.5 0-4.5 2-4.5 4.5h9c0-2.5-2-4.5-4.5-4.5z" />
          </svg>
        </div>
      ),
      position: { x: 100, y: 340 },
      action: () => openWindow("about", "About Me", <AboutWindow />),
    },
    {
      id: "skills",
      name: "Skills",
      icon: (
