          setTerminalHistory((prev) => [
            ...prev,
            `ubuntu-developer@portfolio:~$ ${command}`,
            "Opening X (Twitter) profile...",
            "Redirecting to https://x.com/TanayVasishtha",
            "",
          ])
        } else if (site === "medium") {
          window.open('https://medium.com/@tanayvasishtha', '_blank')
