                    const newIndex = historyIndex - 1
                    setHistoryIndex(newIndex)
                    setCurrentCommand(commandHistory[commandHistory.length - 1 - newIndex])
                } else if (historyIndex === 0) {
                    setHistoryIndex(-1)
                    setCurrentCommand("")
                }
            }
        }
    }

